class Solution30 {
	
	private Map<Integer, Integer> wordIDCountMap;
	private Integer[] wordIDOccurrArr;
	private String str;
	private int wordLength, wordArrLength, concatenatedWordLength;
	private String notFoundStr;
	
	public List<Integer> findSubstring(String s, String[] words) {
		this.str = s;
		this.wordLength = words[0].length();
		this.wordArrLength = words.length;
		this.concatenatedWordLength = wordLength * wordArrLength;
		List<Integer> ansList = new LinkedList<>();

		// special case 1, concatenated words is longer than string
		if ( concatenatedWordLength > s.length()) {
			return ansList;
		} // fi
				
		// special case 2, words array length is 1
		if (words.length == 1) {
			int inx = -1;
			while ((inx = s.indexOf(words[0],inx+1))>-1 && inx < s.length()) {
				ansList.add(inx);
			}//end while	
			return ansList;
		} // fi
		
		populateDataStruct(s,words);		
			
		for (int i = 0; i < words[0].length(); i++) {
			ansList.addAll(sideWindowSearch(i));			
		}//rof

				
		return ansList;
	}// end method
	
	/**
	 * word: 			   h,k,j,k
	 * arr: [a,b,c,d,e,f,g,h,k,j,k,l]
	 *         ^           ^     ^
	 *      winL     mullion     winR
	 * stopChkInx
	 * 
	 * @param startInx - int value denoting where to start checking
	 * @return List of Integers denoting the indices found
	 */	
	private List<Integer> sideWindowSearch(int startInx) {
		List<Integer> ansList = new LinkedList<>();		
		Map<Integer, Integer> currIDCountMap = new HashMap<>();
		Map<Integer, Integer> prevIDCountMap = new HashMap<>();
		int winL = startInx;
		int winR = winL + concatenatedWordLength  - wordLength;
		int stopChkInx = startInx;
		int mullion;
		while (winR <= wordIDOccurrArr.length-wordLength) {
			for (mullion = winR; mullion >= stopChkInx; mullion -= wordLength) {
				Integer currWordID = getWordID(mullion);
				if (currWordID == notFoundStr.hashCode()) {//not match
					if (stopChkInx == winL) {//not match and number of pending elements = full window
						prevIDCountMap.clear(); //no need to have previous count map
					} else { //not match and we will utilize previously checked elements so we keep previous count map
						prevIDCountMap = currIDCountMap;
						currIDCountMap = new HashMap<>();		
					}//fi
					stopChkInx = winR + wordLength;
					winL = mullion + wordLength;
					winR = winL + concatenatedWordLength - wordLength;
					break;
				} else if (mullion > stopChkInx) {//match and mullion > prevWinR, meaning still need to scan more
					currIDCountMap.put(currWordID, currIDCountMap.getOrDefault(currWordID,0) + 1);					
				} else {//match and whole window scanned completely
					currIDCountMap.put(currWordID, currIDCountMap.getOrDefault(currWordID,0) + 1);
					mergeMap(prevIDCountMap, currIDCountMap);
					if (currIDCountMap.equals(wordIDCountMap)) {//map also match exactly
						ansList.add(winL);
					}//fi
					currWordID = getWordID(winL);
					currIDCountMap.put(currWordID, currIDCountMap.get(currWordID) - 1); //decrement wordID count because window is going to shift
					
					//now shift window
					prevIDCountMap = currIDCountMap;
					currIDCountMap = new HashMap<>();
					stopChkInx = winR + wordLength;
					winL += wordLength;
					winR += wordLength;						
				}//fi
			}//rof
		}//end while
		

		return ansList;
	}//end method
	
	/**
	 * precondition
	 * - wordIDOccurrArr initialized and populated
	 * @param inx - int value
	 * @return Integer value , Integer.MIN_VALUE if not matching any of word ID
	 */
	private Integer getWordID(int inx) {
		Integer tmpID = wordIDOccurrArr[inx];
		if (tmpID == null) {
			tmpID = str.substring(inx, inx+wordLength).hashCode();
			if (wordIDCountMap.keySet().contains(tmpID)) {
				wordIDOccurrArr[inx] = tmpID;
			} else {
				wordIDOccurrArr[inx] = notFoundStr.hashCode();
			}//fi
		}//fi		
		return wordIDOccurrArr[inx];
	}//fi
	
	/**
	 * Precondition: 
	 * - words.length > 0
	 * - length of every word > 0
	 * 
	 * postcondition:
	 * - wordOccurrArr initialized and populated
	 * Data type of wordOccurrArr:  Array of Integer
	 * index - the index of string characters
	 * value - word id
	 * 
	 * - wordIDCountMap initialized and popoulated
	 * Data type of wordIDCountMap:  Map of Integer
	 * key - the id of the word
	 * value - word occurrence count 
	 * 
	 * @param s - String
	 * @param words - Array of String
	 */
	private void populateDataStruct(String s, String[] words) {
		this.wordIDCountMap = new HashMap<>();
		this.wordIDOccurrArr = new Integer[s.length()];	
		this.notFoundStr = words[0] + "notfound";
		int wordID;
		Integer count;
		for (String word:words) {
			wordID = word.hashCode();
			if ((count = wordIDCountMap.get(wordID)) != null) {
				wordIDCountMap.put(wordID, count+1);
			} else { //word never added	
				wordIDCountMap.put(wordID, 1);
			}//fi			
		}//rof
				
	}//end method
			
	/**
	 * precondition:
	 * - both maps not null
	 * 
	 * @param map1 - Map<Integer, Integer> type
	 * @param map2 - Map<Integer, Integer> type
	 */
    private void mergeMap(Map<Integer, Integer> map1, Map<Integer, Integer> map2) {  	
    	for (Entry<Integer, Integer> entry:wordIDCountMap.entrySet()) {
    		map2.merge(entry.getKey(), map1.getOrDefault(entry.getKey(), Integer.valueOf(0)), Integer::sum);
    	}//rof
    }//end method       
}// end class


class Solution30_v2 {
	
	static Map<Integer, Integer> wordIDCountMap;
	static Integer[] wordIDOccurrArr;
	static List<Integer> ansList;
	
	public List<Integer> findSubstring(String s, String[] words) {
		int wordLength = words[0].length();
		int wordArrLength = words.length;
		int concatenatedWordLength = wordLength * wordArrLength;
		ansList = new LinkedList<>();

		// special case 1, concatenated words is longer than string
		if ( concatenatedWordLength > s.length()) {
			return ansList;
		} // fi
				
		// special case 2, words array length is 1
		if (words.length == 1) {
			int inx = -1;
			while ((inx = s.indexOf(words[0],inx+1))>-1 && inx < s.length()) {
				ansList.add(inx);
			}//end while	
			return ansList;
		} // fi
		
		populateDataStruct(s,words);		
			
		for (int i = 0; i < words[0].length(); i++) {
			ansList.addAll(sideWindowSearch(i, words[0].length(), concatenatedWordLength));			
		}//rof

				
		return ansList;
	}// end method
	
	/**
	 * word: 			   h,k,j,k
	 * arr: [a,b,c,d,e,f,g,h,k,j,k,l]
	 *         ^           ^     ^
	 *      winL     mullion     winR
	 * stopChkInx
	 *
	 * 
	 * 
	 * @param startInx
	 * @param wordLength
	 * @param concatenatedWordLength
	 * @return
	 */	
	private List<Integer> sideWindowSearch(int startInx, int wordLength, int concatenatedWordLength) {
		List<Integer> ansList = new LinkedList<>();		
		Map<Integer, Integer> currIDCountMap = new HashMap<>();
		Map<Integer, Integer> prevIDCountMap = new HashMap<>();
		int winL = startInx;
		int winR = winL + concatenatedWordLength  - wordLength;
		int stopChkInx = startInx;
		int mullion;
		while (winR < wordIDOccurrArr.length) {
			for (mullion = winR; mullion >= stopChkInx; mullion -= wordLength) {
				Integer currWordID = getWordID(mullion);
				if (currWordID == null) {//not match
					if (stopChkInx == winL) {//not match and number of pending elements = full window
						prevIDCountMap.clear(); //no need to have previous count map
					} else { //not match and we will utilize previously checked elements so we keep previous count map
						prevIDCountMap = currIDCountMap;
						currIDCountMap = new HashMap<>();						
					}//fi
					stopChkInx = winR + wordLength;
					winL = mullion + wordLength;
					winR = winL + concatenatedWordLength - wordLength;
					break;
				} else if (mullion > stopChkInx) {//match and mullion > prevWinR, meaning still need to scan more
					currIDCountMap.put(currWordID, currIDCountMap.getOrDefault(currWordID,0) + 1);					
				} else {//match and whole window scanned completely
					currIDCountMap.put(currWordID, currIDCountMap.getOrDefault(currWordID,0) + 1);
					mergeMap(prevIDCountMap, currIDCountMap);
					if (currIDCountMap.equals(wordIDCountMap)) {//map also match exactly
						ansList.add(winL);
					}//fi
					currWordID = getWordID(winL);
					currIDCountMap.put(currWordID, currIDCountMap.get(currWordID) - 1); //decrement wordID count because window is going to shift
					
					//now shift window
					prevIDCountMap = currIDCountMap;
					currIDCountMap = new HashMap<>();
					stopChkInx = winR + wordLength;
					winL += wordLength;
					winR += wordLength;						
				}//fi
			}//rof
		}//end while
		

		return ansList;
	}//end method
	
	/**
	 * precondition
	 * - wordIDOccurrArr initialized and populated
	 * @param inx - int value
	 * @return Integer value 
	 */
	private Integer getWordID(int inx) {		
		return wordIDOccurrArr[inx];
	}//fi
	
	/**
	 * Precondition: 
	 * - words.length > 0
	 * - length of every word > 0
	 * 
	 * postcondition:
	 * - wordOccurrArr initialized and populated
	 * Data type of wordOccurrArr:  Array of Integer
	 * index - the index of string characters
	 * value - word id
	 * 
	 * - wordIDCountMap initialized and popoulated
	 * Data type of wordIDCountMap:  Map of Integer
	 * key - the id of the word
	 * value - word occurrence count 
	 * 
	 * @param s - String
	 * @param words - Array of String
	 */
	private void populateDataStruct(String s, String[] words) {
		wordIDCountMap = new HashMap<>();
		wordIDOccurrArr = new Integer[s.length()];		
		int wordID, inx;
		Integer count;
		for (String word:words) {
			wordID = word.hashCode();
			if ((count = wordIDCountMap.get(wordID)) != null) {
				wordIDCountMap.put(wordID, count+1);
			} else { //word never added	
				inx = -1;
				//populate occurrence
				while ((inx = s.indexOf(word,inx+1))>-1 && inx < s.length()) {
					wordIDOccurrArr[inx] = wordID;
				}//end while
				wordIDCountMap.put(wordID, 1);
			}//fi			
		}//rof
				
	}//end method
			
	/**
	 * precondition:
	 * - both maps not null
	 * 
	 * @param map1 - Map<Integer, Integer> type
	 * @param map2 - Map<Integer, Integer> type
	 */
    private void mergeMap(Map<Integer, Integer> map1, Map<Integer, Integer> map2) {  	
    	for (Entry<Integer, Integer> entry:wordIDCountMap.entrySet()) {
    		map2.merge(entry.getKey(), map1.getOrDefault(entry.getKey(), Integer.valueOf(0)), Integer::sum);
    	}//rof
    }//end method       
}// end class


