class Solution32 {

	private static int[] stackArr = new int[30000];
	private int nextStackEleAddr = 0;
	private int localMaxRange = 0;
	private int globalMaxRange = 0;
	private int currRange = 0;
	private boolean isPrevOpPop = false;

	public int longestValidParentheses(String s) {
		byte[] strByteArr = s.getBytes();
		for (int currInx = 0; currInx < s.length(); currInx++) {
			switch (strByteArr[currInx]) {
			case 40:
				push(currInx);
				break;
			case 41:
				pop(currInx);
				break;
			default:
				break;
			}// end switch
			/*
			 * System.out.print("currInx: " + currInx + " char:" + s.charAt(currInx));
			 * System.out.println(" stack ptr: " + nextStackEleAddr + " local:" + localMaxRange + "" + "" + " max:" + globalMaxRange); //
			 */
		} // rof

		// localMaxRange is non-zero if there are some unclosed left braces
		if (localMaxRange > globalMaxRange) {
			globalMaxRange = localMaxRange;
		} // fi

		return globalMaxRange;
	}// end method

	/**
	 * Precondition: - stackArray is not null
	 * 
	 * @param inx - int value denoting the index value to be stored
	 */
	private void push(int inx) {
		if (!isPrevOpPop) {
			stackArr[nextStackEleAddr] = inx;
		} // fi
		nextStackEleAddr++;
		isPrevOpPop = false;
	}// end method

	/**
	 * Precondition: - stackArray is not null
	 * 
	 * @param inx - int value denoting the index value to be compared
	 */
	private void pop(int inx) {
		isPrevOpPop = true;
		if (nextStackEleAddr > 0) {
			nextStackEleAddr--;
			currRange = inx - stackArr[nextStackEleAddr] + 1;
			if (currRange > localMaxRange) {
				localMaxRange = currRange;
			} // fi
		} else { // a misformed parentheses arrangement has happened
			// check of local range is > global max range
			if (localMaxRange > globalMaxRange) {
				globalMaxRange = localMaxRange;
			} // fi
			localMaxRange = 0;
			isPrevOpPop = false;
		} // fi
	}// end method

	public void init() {
		nextStackEleAddr = 0;
		globalMaxRange = 0;
		currRange = 0;
		localMaxRange = 0;
		isPrevOpPop = false;
	}// end method
}// end class

class Solution32_v1 {
	public int longestValidParentheses(String s) {
		Stack<Integer> st = new Stack<>();
		int ans = 0;
		st.push(-1);
		for (int i = 0; i < s.length(); i++) {
			if (s.charAt(i) == '(') {
				st.push(i);
			} else {
				if (!st.isEmpty()) {
					st.pop();
				}
				if (!st.isEmpty()) {
					ans = Math.max(i - st.peek(), ans);
				} else {
					st.push(i);
				}
			}
		}
		return ans;
	}
}
