class Solution459_v2 {

    public boolean repeatedSubstringPattern(String s) {
    	
    	for (int lastPatInx =  s.length()/2; lastPatInx > 0; lastPatInx--) {
    		if (s.length()%lastPatInx != 0) {
    			continue;
    		}//fi
    		String[] splitArr = s.split(s.substring(0,lastPatInx));
    		if (splitArr.length == 0) {
    			return true;
    		}//fi
    	}//rof
        return false;
    }//end method
}//end class


class Solution459 {

    public boolean repeatedSubstringPattern(String s) {  
    	for (int lastPatInx =  s.length()/2; lastPatInx > 0; lastPatInx--) {
    		if (s.length()%lastPatInx != 0) {
    			continue;
    		}//fi
    		boolean isRepeating = true;
    		for (int j = lastPatInx; j < s.length() && isRepeating; j+= lastPatInx) {
    			//isRepeating &= s.regionMatches(j, s.substring(0, lastPatInx), 0, lastPatInx); 
    			isRepeating &= s.startsWith(s.substring(0, lastPatInx), j);
    		}//rof
    		if (isRepeating) {
    			return true;
    		}//fi
    	}//rof
        return false;
    }//end method
}//end class

